package java_project;

public class break_switch_credit {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
   
	int credit_card=600;
	
	switch (credit_card) {
	
	case 500:
	System.out.println("your credit score is low");
	break;
	
	case 600:
	System.out.println("your credit score is medium");
	break;
	
	case 700:
	System.out.println("your credit score is high");
	break;
	
	case 800:
	System.out.println("your credit is the highest position");
	break;
	
		
	default:
	System.out.println("I do not know your credit score");
	break;
	}
	
		
		
		
		
		
		
		
		
		
	}

}
