package java_project;

public class if_else_credit{

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	int x=600;
	int y=550;
	int z=800;
	
	if (x>y)
	{
	System.out.println("y credit score is lower than x");
	}
	else
	{
	System.out.println("x credit score is lower than y");
	}
	if (z>y)
	{
	System.out.println("z credit score is higher than y" );
	}
	else
	{
	System.out.println("y credit score is higher than z");
	}
	if (z>x)
	{
	System.out.println("z credit score is higher than x");
	}
	else
	{
	System.out.println("x credit score is higher than z");
	}
		
		
		
	}
}
		
		
		
		
		
		
	


